import * as React from "react";
import { Text, StyleSheet, View } from "react-native";
import { Image } from "expo-image";
import { Button, TextInput as RNPTextInput } from "react-native-paper";
import { useNavigation } from "@react-navigation/native";

const IPhone1422 = () => {
  const navigation = useNavigation();

  return (
    <View style={styles.iphone1422}>
      <Text style={[styles.newBundle, styles.newBundleTypo]}>New Bundle</Text>
      <Image
        style={[styles.icon, styles.iconLayout]}
        contentFit="cover"
        source={require("../assets/icon.png")}
      />
      <Image
        style={[styles.iphoneXTopNavbarLight, styles.iconLayout]}
        contentFit="cover"
        source={require("../assets/iphone-x--top-navbar--light4.png")}
      />
      <View style={styles.textFields}>
        <Text
          style={[styles.addAPersonalized, styles.newBundleTypo]}
        >{`Add a personalized 
message to your 
contributors.`}</Text>
      </View>
      <Button
        style={styles.next}
        mode="contained"
        labelStyle={styles.nextBtn}
        onPress={() => navigation.navigate("IPhone1425")}
        contentStyle={styles.nextBtn1}
      >
        Preview Email
      </Button>
      <View style={styles.homeIndicatorLight}>
        <View style={[styles.homeIndicator, styles.saveAsDraftPosition]} />
      </View>
      <RNPTextInput
        style={styles.egHiFor}
        mode="outlined"
        placeholderTextColor="#cdcdcd"
        theme={{
          fonts: { regular: { fontFamily: "Inter", fontWeight: "Semi Bold" } },
          colors: { text: "#cdcdcd" },
        }}
      />
      <Button
        style={[styles.saveAsDraft, styles.saveAsDraftPosition]}
        mode="elevated"
        labelStyle={styles.saveAsDraftBtn}
        contentStyle={styles.saveAsDraftBtn1}
      >
        Save as Draft
      </Button>
    </View>
  );
};

const styles = StyleSheet.create({
  nextBtn: {
    color: "#fff",
    fontSize: 15,
    fontWeight: "700",
    fontFamily: "Inter-Bold",
  },
  nextBtn1: {
    height: "6.64%",
    width: "83.85%",
  },
  saveAsDraftBtn: {
    color: "#878787",
    fontSize: 15,
    fontFamily: "Inter-Regular",
  },
  saveAsDraftBtn1: {
    marginLeft: -48,
  },
  newBundleTypo: {
    color: "#14304a",
    fontFamily: "Inter-SemiBold",
    fontWeight: "600",
    letterSpacing: 0.1,
  },
  iconLayout: {
    maxHeight: "100%",
    maxWidth: "100%",
    position: "absolute",
    overflow: "hidden",
  },
  saveAsDraftPosition: {
    left: "50%",
    position: "absolute",
  },
  newBundle: {
    left: "38.97%",
    fontSize: 15,
    textAlign: "center",
    top: "10.78%",
    position: "absolute",
  },
  icon: {
    height: "2.13%",
    width: "3.33%",
    right: "87.95%",
    bottom: "87.09%",
    left: "8.72%",
    top: "10.78%",
  },
  iphoneXTopNavbarLight: {
    height: "5.21%",
    width: "96.15%",
    top: "0.59%",
    right: "1.28%",
    bottom: "94.19%",
    left: "2.56%",
  },
  addAPersonalized: {
    fontSize: 28,
    textAlign: "left",
  },
  textFields: {
    marginTop: -260,
    width: "79.23%",
    top: "50%",
    right: "12.82%",
    left: "7.95%",
    position: "absolute",
  },
  next: {
    top: "87.44%",
    right: "7.95%",
    bottom: "5.92%",
    left: "8.21%",
    position: "absolute",
  },
  homeIndicator: {
    marginLeft: -66.5,
    bottom: 8,
    borderRadius: 100,
    backgroundColor: "#191052",
    width: 134,
    height: 5,
  },
  homeIndicatorLight: {
    height: "4.98%",
    width: "99.23%",
    top: "95.02%",
    right: "0%",
    bottom: "0%",
    left: "0.77%",
    position: "absolute",
  },
  egHiFor: {
    width: "82.05%",
    top: "34.6%",
    left: "8.72%",
    position: "absolute",
  },
  saveAsDraft: {
    top: 693,
  },
  iphone1422: {
    backgroundColor: "#fafafa",
    flex: 1,
    width: "100%",
    height: 844,
    overflow: "hidden",
  },
});

export default IPhone1422;
